package week1.day2;

public class CheckNumberIsPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int version = 0;
		if (version > 0) {
			System.out.println("The given integer value version is positive");
		} else if (version == 0) {
			System.out.println("The given integer value is neither positive nor negative, it is equal to zero");
		} else {

			System.out.println("The given integer value version is negative");
		}

	}

}
